clear all%c = -0.096+0.735i;c = 0.214+0.62i;%c = -0.096+0.735i;% c = 0.285+2.13i;q = (1 + sqrt(4*abs(c)))/2;array_size = 500;x = linspace(-q,q,array_size);y = x;[a,b] = meshgrid(x,y);w = a + i*b;m = zeros(array_size,array_size);for t = 1:300	w = ((w.^2)+c);  	m = m + (abs(w) < q);endfor%colormap(jet)colormap([ocean() ; flipud(ocean())])imagesc(x,y,abs(m))title['C=0.214+0.62i']